REPLACE INTO script_lines VALUES ('9001', '9002', NULL, '9', '21', 'About.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9002', '9003', NULL, '8', '21', 'Thank you for playing Mystery Story, Episode 1.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9003', '9004', NULL, '8', '21', 'We are Team 7 Productions; a motley crew of students from CS3216.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9004', '9005', NULL, '8', '21', 'CS3216 is a module known for producing cool projects.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9005', '9006', NULL, '8', '21', 'And this is no exception.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9006', '9007', NULL, '8', '21', 'It\'s an interactive novel, a story-telling game at its heart.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9007', '9008', NULL, '8', '21', 'But it\'s also an engine that, while simple, allows for much cool stuff.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9008', NULL, '66901', '8', '21', 'Like so. Isn\'t our app cool?', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9009', '9010', NULL, '8', '21', 'Thank you for the compliment.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9010', '9011', NULL, '8', '21', 'Team 7 Productions consists of four stalwart, hardy young men&#8230;', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9011', '9012', NULL, '8', '21', '&#8230;who slaved through much toil and sweat to bring you this wonderful, polished product.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9012', '9013', NULL, '8', '21', 'They include:', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9013', '9014', NULL, '8', '21', 'They include:', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9014', '9015', NULL, '8', '21', 'Leontius A. P. A leader of men, and a writer of code.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9015', '9016', NULL, '8', '21', 'He gathers and directs his soldiers under his green banner of Trello.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9016', '9017', NULL, '8', '21', 'Vaarnan D., entrepreneur extraordinaire.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9017', '9018', NULL, '8', '21', 'Also an excellent writer of code, and a fantastic diplomat.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9018', '9019', NULL, '8', '21', 'Darren F., the scribe.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9019', '9020', NULL, '8', '21', 'Writer of writings, and incendiary firestarter who pitched the first stone.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9020', '9021', NULL, '8', '21', 'And our asset producer, Haoyuan J.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9021', '9022', NULL, '8', '21', 'Primary artist; designer, and game meta enthusiasts.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9022', '9023', NULL, '8', '21', 'Not forgetting the doctor who started this with an idea.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9023', '9024', NULL, '8', '21', 'Dr. Dana Elliott, of the AED Corps', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9024', '9025', NULL, '8', '21', 'And props to the two men who rescued the app from failure and oblivion.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9025', '9026', NULL, '8', '21', 'Professor \"The Evil\" Ben Leong', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9026', '9027', NULL, '8', '21', 'And The Man Who Saved 3216, Mr Goh Kok Wee.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9027', '9028', NULL, '8', '21', 'This game was written in Json and Sencha.', 'lower', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('9028', '9029', NULL, '8', '21', 'Content produced in the dregs of coffee-cups and feverish delusions (but written in Excel).', 'lower', NULL, NULL, NULL, NULL, 'dark');
