REPLACE INTO script_lines VALUES ('8000', '8001', NULL, '8', '19', 'Hello, I\'m Semyon Lavisham, and I\'ll be your assistant for today.', NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO script_lines VALUES ('8001', '8002', NULL, '8', '19', 'Welcome to the AED Simulation Minigame.', 'lower', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8002', '8003', NULL, '8', '19', 'This section will involve simulation of several scenarios where an AED may be applied.', 'lower', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8003', '8004', NULL, '8', '19', 'You will be prompted both by on-screen directions&#8230;', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8004', '8005', NULL, '8', '19', '&#8230;as well as audio recordings from an actual AED machine, on the appropriate course of action to take.', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8005', '8006', NULL, '8', '19', 'The wrong choice of action will be accompanied by a buzzer&#8230;', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8006', '8007', NULL, '8', '19', '&#8230;and you will be returned to the previous choice.', 'upper', NULL, NULL, NULL, NULL, 'deck');
REPLACE INTO script_lines VALUES ('8007', '8008', NULL, '8', '19', 'Now, you may select your options;', 'upper', NULL, NULL, NULL, NULL, 'deck');
REPLACE INTO script_lines VALUES ('8008', '8009', NULL, '8', '19', 'Training will lead you to a simple tutorial level, where the basic functions of an AED will be demonstrated.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8009', '8010', NULL, '8', '19', 'In Scenarios, we run through several possible scenarios where functionality may be different.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8010', '8011', NULL, '8', '19', 'And finally, in Test Yourself, you will be faced with a random scenario and need to give the correct response.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8011', NULL, '66800', '8', '19', 'Make your choice, now,', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8012', '8013', NULL, '8', '21', 'Training.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8013', '8014', NULL, '8', '21', 'Welcome to Training mode.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8014', '8015', NULL, '8', '21', 'Here, you will learn more about the AED.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8015', '8016', NULL, '8', '21', 'The first thing to note is that there is no harm in attempting the use of an AED.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8016', '8017', NULL, '8', '21', 'The AED will analyse the patient\'s heartbeat, and determine if a shock is appropriate or necessary.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8017', '8018', NULL, '8', '21', 'You can never kill a person with an AED, so, in most cases,', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8018', '8019', NULL, '8', '21', '&#8230;it\'s better, if in doubt, to apply the AED.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8019', '8020', NULL, '8', '21', 'An AED is a defibrillator, which basically applies an electrical shock to the heart.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8020', '8021', NULL, '8', '21', 'The shock will only be delivered in cases where a shock to the heart will be appropriate.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8021', NULL, '66801', '8', '21', 'When will the shock be delivered?', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8022', '8023', NULL, '8', '21', 'Nonsense Choice', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8023', '8024', NULL, '8', '21', 'No; a shock to the heart will not be useful in that situation', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8024', '8021', NULL, '8', '21', 'Try again!', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8025', '8026', NULL, '8', '21', 'when somebody is experiencing in a cardiac arrest', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8026', '8027', NULL, '8', '21', 'Correct!', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8027', '8028', NULL, '8', '21', 'The shock delivered is intended to steady an irregular heartbeat&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8028', '8029', NULL, '8', '21', '&#8230;as in cases such as arrhyhmia or cardiac arrest.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8029', '8030', NULL, '8', '21', 'A stroke is a lack of blood to the head, and cannot be solved by a shock.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8030', '8031', NULL, '8', '21', 'And if the heart is stopped, it is too late to deliver the shock.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8031', '8032', NULL, '8', '21', 'Very good!', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8032', '8033', NULL, '8', '21', 'So now, when we have a bystander suffering an impending cardiac arrest.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8033', NULL, '66802', '8', '21', 'Here is an AED. What shall we do?', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8034', '8035', NULL, '8', '21', 'Perform CPR', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8035', '8036', NULL, '8', '21', 'In most cases, this course of action would be correct.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8036', '8037', NULL, '8', '21', 'But since we have an AED, we should apply it immediately.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8037', '8038', NULL, '8', '21', 'apply the AED', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8038', '8039', NULL, '8', '21', 'Correct! We would normally apply CPR, but since we have an AED we should apply it to analyse the heart rhythm immediately.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8039', '8040', NULL, '8', '21', 'We apply the pads and allow the AED to analyze the heart rhythm of the patient.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8040', '8041', NULL, '8', '21', 'There are two pads for the AED, one for each side of the body.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8041', NULL, '66803', '8', '21', 'Where does the Left Pad go?', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8042', '8043', NULL, '8', '21', 'Nonsense', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8043', '8044', NULL, '8', '21', 'Alas, that is incorrect.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8044', '8045', NULL, '8', '21', 'Take a look at the picture, and try again.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8045', '8046', NULL, '8', '21', 'Under the armpit.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8046', '8047', NULL, '8', '21', 'Correct!', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8047', '8048', NULL, '8', '21', 'The left pad is placed under the left armpit.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8048', '8049', NULL, '8', '21', 'And the right pad?', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8049', '8050', NULL, '8', '21', 'Nonsense', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8050', '8051', NULL, '8', '21', 'Alas, that is incorrect.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8051', '8052', NULL, '8', '21', 'Take a look at the picture, and try again.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8052', '8053', NULL, '8', '21', 'On the collarbone.', 'upper', NULL, 'Yes.', NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8053', '8054', NULL, '8', '21', 'Correct!', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8054', '8055', NULL, '8', '21', 'The right pad is placed on the chest, in the region of the right collarbone.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8055', '8056', NULL, '8', '21', 'We must remove the clothes before applying the pads, as the electric shock may burn the clothes.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8056', '8057', NULL, '8', '21', 'More importantly, the charge may not be delivered properly to the patient.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8057', '8058', NULL, '8', '21', 'Once we attached the pads, we press the button.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8058', '8059', NULL, '8', '21', 'At this point, the AED will deliver a message.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8059', '8060', NULL, '8', '21', '<font face=\"Courier New\">Analysing Heart Rhythm.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8060', '8061', NULL, '8', '21', '<font face=\"Courier New\">Do not touch patient.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8061', '8062', NULL, '8', '21', 'What should we do now?', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8062', '8063', NULL, '8', '21', 'Perform CPR', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8063', '8064', NULL, '8', '21', 'Well&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8064', '8065', NULL, '8', '21', '<font face=\"Courier New\">Analysing interrupted.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8065', '8066', NULL, '8', '21', '<font face=\"Courier New\">Stop Motion. Do not touch Patient.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8066', '8067', NULL, '8', '21', 'Oops!', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8067', '8068', NULL, '8', '21', 'While CPR is performed in conjunction with AED, the AED needs to be static in order to analyze the heart rhythm.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8068', '8069', NULL, '8', '21', 'Do nothing.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8069', '8070', NULL, '8', '21', 'Correct!', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8070', '8071', NULL, '8', '21', 'Now, once analysis is complete, one of two directives will be given.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8071', '8072', NULL, '8', '21', '<font face=\"Courier New\">No shock advised.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8072', '8073', NULL, '8', '21', 'In this case, the patient\'s heartbeat is not irregular.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8073', '8074', NULL, '8', '21', 'It may be a normal heartbeat, in which case he is choking, or the heartbeat may have stopped.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8074', '8075', NULL, '8', '21', 'The AED will advise on alternative courses of action.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8075', '8076', NULL, '8', '21', '<font face=\"Courier New\">Check airway. Check breathing.</font>', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8076', '8077', NULL, '8', '21', '<font face=\"Courier New\">If needed, perform CPR now.</font>', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8077', '8078', NULL, '8', '21', 'In the alternative case, the patient is suffering from cardiac arrest.', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8078', '8079', NULL, '8', '21', 'In this case, the direction given by the AED will be different.', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8079', '8080', NULL, '8', '21', '<font face=\"Courier New\">Shock advised.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8080', '8081', NULL, '8', '21', 'This will be followed by a loud increasing pitch&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8081', '8082', NULL, '8', '21', '&#8230;as the machine builds up the charge to deliver the shock.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8082', '8083', NULL, '8', '21', 'Finally, the machine will prompt;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8083', '8084', NULL, '8', '21', '<font face=\"Courier New\">Press shock button.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8084', '8085', NULL, '8', '21', 'During this time it is best not to touch the patient&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8085', '8086', NULL, '8', '21', '&#8230;as the current may pass through you, through contact.', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8086', '8087', NULL, '8', '21', 'The AED will deliver the shock. And then&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8087', '8088', NULL, '8', '21', '<font face=\"Courier New\">Shock 1 delivered.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8088', '8089', NULL, '8', '21', '<font face=\"Courier New\">It is now safe to touch the patient</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8089', '8090', NULL, '8', '21', '<font face=\"Courier New\">Continue CPR now. Continue CPR for one-minute forty five-seconds.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8090', '8091', NULL, '8', '21', '&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8091', '8092', NULL, '8', '21', '<font face=\"Courier New\">Continue CPR for one-minute thirty seconds.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8092', '8093', NULL, '8', '21', '&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8093', '8094', NULL, '8', '21', '<font face=\"Courier New\">Continue CPR for one-minute fifteen seconds.</font>', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8094', '8095', NULL, '8', '21', '&#8230;', 'upper', NULL, NULL, NULL, NULL, 'corridor');
REPLACE INTO script_lines VALUES ('8095', '8096', NULL, '8', '21', 'And so on, for the next minute. After which&#8230;', 'upper', NULL, NULL, NULL, NULL, 'dock');
REPLACE INTO script_lines VALUES ('8096', '8097', NULL, '8', '21', '<font face=\"Courier New\">Analysing Heart Rhythm.</font>', 'upper', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('8097', '8098', NULL, '8', '21', '<font face=\"Courier New\">Do not touch patient.</font>', 'upper', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('8098', '8099', NULL, '8', '21', 'The AED will analyse the patient\'s heartbeat again, and determine if further shocks are necessary.', 'upper', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('8099', '8100', NULL, '8', '21', '<font face=\"Courier New\">Shock advised.</font>', 'upper', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('8100', '8101', NULL, '8', '21', '<font face=\"Courier New\">Press shock button.</font>', 'upper', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('8101', '8102', NULL, '8', '21', '<font face=\"Courier New\">Shock 2 delivered.</font>', 'upper', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('8102', '8103', NULL, '8', '21', '<font face=\"Courier New\">It is now safe to touch the patient</font>', 'upper', NULL, NULL, NULL, NULL, 'dark');
REPLACE INTO script_lines VALUES ('8103', '8104', NULL, '8', '21', '<font face=\"Courier New\">Continue CPR now. Continue CPR for one-minute forty five-seconds.</font>', 'upper', NULL, NULL, NULL, NULL, 'dark');
